mapper.select:
select num, userid, title,author,content,to_char(writeday,'YYYY/MM/DD') writeday,readCnt ,board_image
from Myboard
where num=#{num}

mapper.update:
update Myboard
set readCnt=readCnt+1
where num=#{num}

update myboard
set title=#{title}, author=#{author}, content=#{content}
where num=#{num}
